import React from 'react'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import './sign-up.css'

class SignUpComponent extends React.Component{

    constructor(){
        super()

        this.state = {
            nickName: '',
            email: '',
            password: ''
        }
    }

    handleChange = e => {
        const {name, value} = e.target
        this.setState({ [name]: value })
    }

    handlePasswordConfirmation = e => {
        const { value } = e.target
    }

    handleSubmit = e => {
        e.preventDefault()
        console.log(this.state)
    }

    render(){
        return(
            <div className='sign-up-component'>
                <h2>Or, Sign up here</h2>
                <span>We'll only need your email and a password.</span>

                <Form onSubmit={this.handleSubmit}>
                    <Form.Control type="text" name="nickName" placeholder="Nickname" onChange={this.handleChange}  required/>
                    <Form.Control type="email" name="email" placeholder='Email' onChange={this.handleChange} required />
                        
                    <Form.Control type="password" name="password" placeholder='Password' onChange={this.handleChange} required />
                    <Form.Control type="password" name="confirm-password" placeholder='Confirm Password' required />
                    <Button variant='dark' className='sign-in-buttons' type='submit'> Sign Up </Button>    
                </Form>
            </div>
        )
    }
}

export default SignUpComponent
